import React, { Component } from 'react';
import './App.css';
import './Components/ParentComp';
import ParentComp from './Components/ParentComp';

class App extends Component {

  constructor(){
    super();
    this.state ={
      turn: 'X',
      gameEnd: false,
      board: Array(9).fill(''),
      total:0,
      winner:undefined
    }
  }

  selectPlayer(event){
    event.preventDefault();
    console.log(event.target.player.value);
    this.setState({
      turn: event.target.player.value,
    })
  }

clicked(event){
  if(this.state.gameEnd == true){
    return;
  }
  if(this.state.board[event.dataset.square] == ''){
  this.state.board[event.dataset.square] = this.state.turn;
  event.innerText = this.state.turn;
  this.setState({
    turn: this.state.turn == 'X'? 'O':'X',
    board: this.state.board
  })
  this.state.total++
  var res = this.checkWinner();

  if(res == 'X'){
    this.state.gameEnd = true;
    this.state.winner = "Match won by x";
  }
  else  if(res == 'O'){
    this.state.gameEnd = true;
    this.state.winner = "Match won by o";
  }
  else if (this.state.total == 9){
    this.state.gameEnd = true;
    this.state.winner = "Match is draw";
  }

}
  console.log(this.state.board);
}

checkWinner(){
  var moves = [[0, 3, 6], [1, 4, 7], [2, 5, 8], [0, 4, 8], [2, 4, 6], [0, 1, 2], [3, 4, 5], [6, 7, 8]];
  var board = this.state.board;
    for(let i=0;i<moves.length;i++) {
      if(board[moves[i][0]] == board[moves[i][1]] && board[moves[i][1]] == board[moves[i][2]])
          return board[moves[i][0]];
    }
    if(this.state.total ==9){
      return 'draw';
    }

}
  render() {
    return (
      <div id="game">
        <ParentComp/>
          <div id="status">{this.state.winner}</div>
          <div id="head">
              Tic tac toe
          </div>
         <form onSubmit = {(e)=> {this.selectPlayer(e)}}>
           <label>
             Player x
             <input type="radio" name="player" value = "X"/>
           </label>

           <label>
             Player O
             <input type="radio" name="player" value = "O"/>

           </label>
           <input type="submit" value = "Start"/>
         </form>
          <div id="board" onClick={(e)=>this.clicked(e.target)}>
              <div className="square" data-square="0"></div>
              <div className="square" data-square="1"></div>
              <div className="square" data-square="2"></div>
              <div className="square" data-square="3"></div>
              <div className="square" data-square="4"></div>
              <div className="square" data-square="5"></div>
              <div className="square" data-square="6"></div>
              <div className="square" data-square="7"></div>
              <div className="square" data-square="8"></div>
          </div>

      </div>      
    );
  }
}
export default App;
